import React, { useEffect, useState } from "react";
import styled from "styled-components";
import symbol from "../../../assets/vote-logo.png";
import vote from "../../../assets/vote.png";
import success from "../../../assets/success.png";
import faild from "../../../assets/faild.png";
import not from "../../../assets/not.png";

const VCContainer = styled.div`
  background-color: #ffffff;
  border-radius: 4px;
  padding: 18px 10px 10px;
  display: flex;
  flex-direction: column;
  box-shadow: 0 3px 10px rgb(0 0 0 / 0.2);
  margin-bottom: 14px;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  & > .vote-logo {
    background-image: url(${symbol});
    background-size: cover;
    background-repeat: no-repeat;
    width: 89px;
    height: 89px;
  }

  & > .title-card {
    width: 70%;
    .title {
      margin: 0;
      color: #707070;
      font-size: 3.72vw;
      font-weight: 200;
      position: relative;
      padding-right: 20px;
      margin-bottom: 10px;
      &:before {
        content: "";
        display: flex;
        position: absolute;
        width: 32px;
        height: 32px;
        background-image: url(${vote});
        background-size: contain;
        background-repeat: no-repeat;
        right: -10px;
        top: -3px;
      }
    }
    h2 {
      margin: 0;
      color: #707070;
      font-size: 4.65vw;
      font-weight: 400;
      margin-bottom: 10px;
    }
    .date {
      margin: 0;
      color: rgba(0, 0, 0, 0.2);
      font-size: 2.79vw;
      font-weight: bold;
    }
  }
`;

const Statistics = styled.div`
  display: flex;

  gap: 70px;
  padding-right: 50px;
  padding-top: 10px;
  border-top: 1px solid #d8d8d8;
  margin-top: 17px;
  justify-content: center;
  padding-bottom:13px;

`;

const Success = styled.div`
  color: #6cbba9;
  font-weight: 300;
  font-size: 5.58vw;
  position: relative;
  &:before {
    content: "";
    display: block;
    position: absolute;
    width: 41px;
    height: 41px;
    background-image: url(${success});
    background-size: contain;
    background-repeat: no-repeat;
    right: -50px;
    top: -6px;
  }
  &.active {
    font-weight: bold;
    &:after {
      content: "";
      display: block;
      position: absolute;
      width: 80px;
      height: 4px;
      background-color: #6cbba9;
      bottom: -13px;
      right: -50px;
    }
  }
`;

const Faild = styled.div`
  color: #ffa5a5;
  font-weight: 300;
  font-size: 5.58vw;
  position: relative;
  &:before {
    content: "";
    display: block;
    position: absolute;
    width: 41px;
    height: 41px;
    background-image: url(${faild});
    background-size: contain;
    background-repeat: no-repeat;
    right: -50px;
    top: -6px;
  }
  &.active {
    font-weight: bold;
    &:after {
      content: "";
      display: block;
      position: absolute;
      width: 80px;
      height: 4px;
      background-color: #ffa5a5;
      bottom: -13px;
      right: -50px;
    }
  }
`;

const Not = styled.div`
  color: #d8d8d8;
  font-weight: 300;
  font-size: 5.58vw;
  position: relative;

  &:before {
    content: "";
    display: block;
    position: absolute;
    width: 41px;
    height: 41px;
    background-image: url(${not});
    background-size: contain;
    background-repeat: no-repeat;
    right: -50px;
    top: -6px;
  }

  &.active {
    font-weight: bold;
    &:after {
      content: "";
      display: block;
      position: absolute;
      width: 80px;
      height: 4px;
      background-color: #d8d8d8;
      bottom: -13px;
      right: -50px;
    }
  }
`;

const EnvoyGallery=styled.div`
  background-color:${props=>props.color};
  border-radius:4px;
`

export default function VoteCard() {
  const [active, setActive] = useState(0);
  const [color,SetColor]=useState('#DFF5F0');


  useEffect(()=>{
    if(active===1){
      SetColor("#FFD5D5");
    }
    else if(active===2){
      SetColor("#EAEAEA");
    }
    else if(active===0){
      SetColor("#DFF5F0");
    }
  },[active]);


  return (
    <VCContainer>
      <CardHeader>
        <div className="vote-logo"></div>
        <div className="title-card">
          <p className="title">رأی‌گیری</p>
          <h2>کلیات لایحۀ بودجۀ سال ۱۴۰۱</h2>
          <p className="date">۲۹ اسفند ۱۴۰۰</p>
        </div>
      </CardHeader>
      <Statistics>

        <Success
          onClick={() => setActive(0)}
          className={active === 0 ? "active" : ""}
        >
          124
        </Success>
        <Faild
          onClick={() => setActive(1)}
          className={active === 1 ? "active" : ""}
        >
          65
        </Faild>
        <Not
          onClick={() => setActive(2)}
          className={active === 2 ? "active" : ""}
        >
          23
        </Not>




      </Statistics>

      <EnvoyGallery color={color}>
            <h1>gamiasd</h1>
        </EnvoyGallery>
    </VCContainer>
  );
}
