    position:relative;
    span{
        background-image: url(${mag});
        background-size: contain;
        background-repeat: no-repeat;
        width:64px;
        height:64px;
    }
    h1{
        color:#095644;
        font-size:4.65vw;
        font-weight:300;
        text-align:center;
    }
    &:after{
        content: "";
        display: flex;
        position: absolute;
        background-image: url(${leftArrow});
        background-size: cover;
        background-repeat: no-repeat;
        width: 5px;
        height: 10px;
        left:-8px;
    }

`;


