import React from "react";
import styled from "styled-components";
import pic from "../../../assets/pic.png";

const EnvoyCard = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 15px 25px 15px 35px;
  background: #ffffff;
  box-shadow: 0px 0px 20px -5px rgba(0, 0, 0, 0.15);
  border-radius: 4px;
  margin-bottom: 10px;
`;

const EnvoyImage = styled.div`
  width: 87px;
  height: 90px;
  border-radius: 87px;
  border: 3px solid #9f9f9f;
  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const Content = styled.div`
  width: 65%;
  display: flex;
  flex-direction: column;
  h3{
    color:#707070;
    font-size:4.65vw;
    font-weight:bold;
    margin:0;
    margin-bottom:7px;
  }
`;

export default function BestEnvoy() {
  return (
    <EnvoyCard>
      <EnvoyImage>
        <img src={pic} />
      </EnvoyImage>
      <Content>
        <h3>علیرضا پاکفطرت</h3>
        <div>
            <p>دماوند و فیروزکوه</p>
            <p> امنیت ملی</p>
        </div>
        <div>
            <p>شفافیت: ٪</p>
            <p>۷۵%</p>
        </div>
      </Content>
    </EnvoyCard>
  );
}
