import React from "react";
import styled from "styled-components";
import data from "../../data.json";
import azmma from "../../assets/azmaa.svg";

const FooterContainer = styled.section`
  background-color: #095644;
  padding: 19px 20px;
  @media (min-width: 480px) {
    padding:30px 10%;
  }
`;

const AboutUs = styled.div`
  display: none;
  @media (min-width: 480px) {
    display: flex;
  }
`;

const ContactUs = styled.div`
  & > .item {
    display: flex;
    margin-bottom: 10px;
    gap: 17px;

    span {
      width: 45px;
      height: 40px;
      img {
        width: 100%;
        height: 100%;
        object-fit: contain;
      }
    }

    .content {
      width: 85%;
      background-color: #f3f3f3;
      border-radius: 4px;
      display: flex;
      a {
        margin: auto 10px;
        color: #095644;
        text-decoration: none;
        width: 100%;
        text-align: left;
        font-size: 24px;
        font-weight: light;
        direction: ltr;
      }
    }
  }

  @media (min-width: 480px) {
    &>.item{
      .content{
        width:35%
      }
    }
  }
`;

const Copyright = styled.div`
  min-height: 42px;
  background-color: #ffffff;
  display: flex;
  p {
    margin: auto;
    color: #cbcbcb;
    white-space: nowrap;
    display: flex;
    div {
      background-image: url(${azmma});
      background-size: contain;
      padding-right: 5px;
      padding-left: 5px;
      opacity: 0.5;
      background-repeat: no-repeat;
      width: 29px;
      height: 24px;
    }
  }
`;

export default function Footer() {
  const contactItem = data.footer.map((x, i) => {
    return (
      <div className="item">
        <span>
          <img src={x.icon} />
        </span>
        <div className="content">
          <a target="_blank" href={x.href}>
            {x.contact}
          </a>
        </div>
      </div>
    );
  });

  return (
    <div>
      <FooterContainer>
        <ContactUs>{contactItem}</ContactUs>
        <AboutUs>
          <h4>کمی در بارۀ ازما:</h4>
          <p>
            لورم ایپسوم متن ساختگی با تولید سادگی نامفهوم از صنعت چاپ، و با
            استفاده از طراحان گرافیک است، چاپگرها و متون بلکه روزنامه و مجله در
            ستون و سطرآنچنان که لازم است، و برای شرایط فعلی تکنولوژی مورد نیاز،
            و کاربردهای متنوع با هدف بهبود ابزارهای کاربردی می باشد، کتابهای
            زیادی در شصت و سه درصد گذشته حال و آینده، شناخت فراوان جامعه و
            متخصصان را می طلبد، تا با نرم افزارها شناخت بیشتری را برای طراحان
            رایانه ای علی الخصوص طراحان خلاقی، و فرهنگ پیشرو در زبان فارسی ایجاد
            کرد، در این صورت می توان امید داشت که تمام و دشواری موجود در ارائه
            راهکارها، و شرایط سخت تایپ به پایان رسد و زمان مورد نیاز شامل
            حروفچینی دستاوردهای اصلی، و جوابگوی سوالات پیوسته اهل دنیای موجود
            طراحی اساسا مورد استفاده قرار گیرد.
          </p>
        </AboutUs>
      </FooterContainer>
      <Copyright>
        <p>
          همۀ حقوق نزد <div></div> محفوظ است.
        </p>
      </Copyright>
    </div>
  );
}
