import React from "react";
import styled from "styled-components";
import data from "../../data.json";

const FooterContainer = styled.section`
  background-color: #095644;
  padding: 19px 20px;
`;

const AboutUs = styled.div`
  display: none;
`;

const ContactUs = styled.div`
  & > .item {
    display: flex;
    margin-bottom: 10px;
    gap: 17px;

    span {
      width: 45px;
      height: 40px;
      img {
        width: 100%;
        height: 100%;
        object-fit: contain;
      }
    }

    .content {
      width: 85%;
      background-color: #f3f3f3;
      border-radius: 4px;
      display:flex;
      a {
        margin:auto 10px;
        color: #095644;
        text-decoration: none;
        width:100%;
        text-align:left;
        font-size:24px;
        font-weight:light;
        direction: ltr;
      }
    }
  }
`;

const Copyright = styled.div`
  min-height:42px;
  background-color:#FFFFFF;
  display:flex;
  p{
    margin:auto;
    color

  }
`;

export default function Footer() {
  const contactItem = data.footer.map((x, i) => {
    return (
      <div className="item">
        <span>
          <img src={x.icon} />
        </span>
        <div className="content">
          <a target="_blank" href={x.href}>
            {x.contact}
          </a>
        </div>
      </div>
    );
  });

  return (
    <div>
      <FooterContainer>
        <ContactUs>{contactItem}</ContactUs>
        <AboutUs></AboutUs>
      </FooterContainer>
      <Copyright><p>همۀ حقوق نزد   <span></span>      محفوظ است.</p></Copyright>
    </div>
  );
}
