import React, { useEffect, useState } from "react";
import data from "../../data.json";
import LOGO from "../../assets/azmaa-logo.png";
import menu from "../../assets/menu.png";
import search from "../../assets/search.png";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
// styled
const TopBar = styled.section`
  background-color: #095644;
  padding: 20px 30px;
  display: flex;
  justify-content: space-between;
`;

const Logo = styled.div`
  width: 31px;
  height: 26px;
  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const LeftSection = styled.div`
  display: flex;
  flex-direction: row;
  gap: 29px;
`;

const Menu = styled.div`
  background-image: url(${menu});
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const MenuList = styled.ul`
  list-style: none;
  margin: 0;
  padding: 0;
  display: ${(props) => (props.open ? "flex" : "none")};
  position: absolute;
  left: 0;
  right: 0;
  top: 67px;
  background-color: #ffffff;
  flex-direction: column;
  padding: 51px 81px;
  gap: 10px;

`;

const List =styled.li`
  width: fit-content;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 10px;
  position:relative;
  &.active{
  &:before{
    content:'';
    display:flex ;
    background-color:#095644;
    border-radius:4px 0px 0px 4px;
    right: -81px;
    position: absolute;
    width: 68px;
    height: 48px;
  }
}
  span {
    background-color: #f3f3f3;
    width: 50px;
    height: 48px;
    border-radius: 4px;
    display: flex;
    img {
      width: 40px;
      height: 40px;
      object-fit: contain;
      margin: auto;
    }
  }

`

const Search = styled.div`
  background-image: url(${search});
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const Panel = styled.div`
  display: none;
`;

export default function Navbar() {
  // const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [active, setActive] = useState(false);

  const menuItem = data.navbar.map((x, i) => {
    return (
      <List key={i} onClick={choiseItem} className={active?"active":""}  >
        <span>
          <img src={active ? x.active_icon : x.icon} />
        </span>{" "}
        {x.title}
      </List>
    );
  });



    function choiseItem (){
      if(active){
        setActive(false)
      }else{
        setActive(true)
      }
    }



  function handelClick() {
    event.stopPropagation();
    // e.stopPropagation();
    if (open) {
      setOpen(false);
    } else {
      setOpen(true);
    }
    return open;
  }

  return (
    <TopBar>
      <Logo>
        <img src={LOGO} alt="logo" />
      </Logo>
      <LeftSection>
        <Panel></Panel>
        <Search></Search>
        <Menu onClick={(e)=>handelClick}>
          <MenuList open={open}>{menuItem}</MenuList>
        </Menu>
      </LeftSection>
    </TopBar>
  );
}
