import React, { useState } from "react";
import data from "../../data.json";
import LOGO from "../../assets/azmaa-logo.png";
import menu from "../../assets/menu.png";
import search from "../../assets/search.png"
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
// styled
const TopBar = styled.section`
  background-color: #095644;
  padding: 20px 30px;
  display: flex;
  justify-content: space-between;
`;

const Logo = styled.div`
  width: 31px;
  height: 26px;
  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const LeftSection = styled.div`
  display: flex;
  flex-direction: row;
  gap: 29px;
`;

const Menu = styled.div`
  background-image: url(${menu});
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const MenuList= styled.ul`
list-style: none;
margin: 0;
padding: 0;
display: ${props=>props.open? "flex":"none"};
position:absolute;
left: 0;
right:0;
top: 67px;
background-color: #FFFFFF;
flex-direction: column;
padding: 51px 81px;
gap: 10px;
li{
  width:fit-content;
}
`

const Search = styled.div`
  background-image: url(${search});
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const Panel =styled.div`
  display:none;
`

export default function Navbar() {
  // const navigate = useNavigate();
  const [open,setOpen]=useState(false);
  const [active,setActive]=useState(false);

  const menuItem = data.navbar.map((x, i) => {
    return <li key={i}><span><img src={active?x.active-icon:x.icon}/></span> {x.title}</li>;
  });

  function handelClick (){
    if(open){
      setOpen(false);
    }else{
      setOpen(true)
    }
    return open;
  }

  return (
    <TopBar>
      <Logo>
        <img src={LOGO} alt="logo" />
      </Logo>
      <LeftSection>
        <Panel></Panel>
        <Search></Search>
        <Menu onClick={handelClick} >
          <MenuList open={open}>{menuItem}</MenuList>
        </Menu>
      </LeftSection>
    </TopBar>
  );
}
