import React, { useState } from "react";
import data from "../../data.json";
import LOGO from "../../assets/azmaa-logo.png";
import menu from "../../assets/menu.png";
import search from "../../assets/search.png";
import close from "../../assets/close.png";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import useWidth from "../../hook/useWidth";

// styled
const TopBar = styled.section`
  background-color: #095644;
  padding: 20px 30px;
  display: flex;
  justify-content: space-between;
  @media(min-width:430px){
    width:90%;
  }
`;

const Logo = styled.div`
  width: 31px;
  height: 26px;
  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const LeftSection = styled.div`
  display: flex;
  flex-direction: row;
  gap: 29px;
`;

const Menu = styled.div`
  background-image: ${(props) =>
    props.open ? `url(${close})` : `url(${menu})`};
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const MenuList = styled.ul`
  list-style: none;
  margin: 0;
  padding: 0;
  display: ${(props) => (props.open ? "flex" : "none")};
  position: absolute;
  left: 0;
  right: 0;
  top: 67px;
  background-color: #ffffff;
  flex-direction: column;
  padding: 51px 81px;
  gap: 10px;
`;

const List = styled.li`
  width: fit-content;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 10px;
  position: relative;
  color: #095644;
  font-size: 20px;
  font-weight: light;
  &.active {
    font-weight: bolder;
    &:before {
      content: "";
      display: "flex";
      background-color: #095644;
      border-radius: 4px 0px 0px 4px;
      right: -81px;
      position: absolute;
      width: 68px;
      height: 48px;
    }
  }

  span {
    background-color: #f3f3f3;
    width: 50px;
    height: 48px;
    border-radius: 4px;
    display: flex;
    img {
      width: 40px;
      height: 40px;
      object-fit: contain;
      margin: auto;
    }
  }
  &:nth-child(3) {
    span {
      img {
        width: 30px;
        height: 30px;
      }
    }
  }
  &:nth-child(5) {
    span {
      img {
        width: 30px;
        height: 30px;
      }
    }
  }
  &:nth-child(6) {
    span {
      img {
        width: 30px;
        height: 30px;
      }
    }
  }

  &:nth-last-child(1) {
    position: absolute;
    bottom: -66px;
    color: #ffaa00;
    font-size: 24px;
    font-weight: bold;
    right: 20px;
  }
`;

const Search = styled.div`
  background-image: url(${search});
  background-size: contain;
  width: 20px;
  height: 20px;
`;

const Panel = styled.div``;

export default function Navbar() {
  const width = useWidth();
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [active, setActive] = useState(0);

  const menuItem = data.navbar.map((x, i) => {
    return (
      <List
        key={i}
        onClick={() => choiseItem(i, x.path)}
        className={i === active ? "active" : ""}
      >
        <span>
          <img src={active === i ? x.active_icon : x.icon} alt={x.title} />
        </span>{" "}
        {x.title}
      </List>
    );
  });

  function choiseItem(num, path) {
    setActive(num);
    navigate(path);
    setOpen(false);
  }

  function handelClick() {
    if (open) {
      setOpen(false);
    } else {
      setOpen(true);
    }
    return open;
  }

  return (
    <TopBar>
      <Logo>
        <img src={LOGO} alt="logo" />
      </Logo>
      <LeftSection>
        {width < 430 ? (
          ""
        ) : (
          <Panel>
            <span></span> <div>ورود به پنل</div>
          </Panel>
        )}

        <Search></Search>
        {width < 430 ? <Menu onClick={handelClick} open={open}></Menu> : ""}
        {width < 430 ? <MenuList open={open}>{menuItem}</MenuList> : ""}
      </LeftSection>
    </TopBar>
  );
}
