import React from "react";
import styled from "styled-components";
import background from "../../../assets/back-controll.png";
import data from "../../../data.json";

const ControllContainer = styled.section`
  display: flex;
  flex-direction: column;
`;

const FilterContainer = styled.div`
  background-image: url(${background});
  background-size: cover;
  background-repeat: no-repeat;
  padding:19px;
  border-radius:2px;
`;

const SearchInput = styled.input`
    width:98%;
    padding:7px 8px;
    border-radius:2px;
    border:none;
    font-size:3.72vw;
    margin-bottom:19px;
    font-family:FontAwesome;
    &:placeholder{
        // color:#D8D8D8;
        color:red;
    }
`

const TabContainer = styled.div`
    display:flex;
    gap:20px;
`



export default function Controller() {
    const controllItem = data.controlPanel.map((x,i)=>{
        <div><span><img src={x.icon}/></span>  <p>{x.name}</p></div>
    })
  return (
    <ControllContainer>
      <FilterContainer>
        <SearchInput type="text" placeholder="&#xF002; جستجو کن..."  />
        <TabContainer>
            {controllItem}
        </TabContainer>
      </FilterContainer>
    </ControllContainer>
  );
}
